            CloseButtonText = "Ok",
            Icon = $"{System.IO.Directory.GetCurrentDirectory()}/wwwroot/icon.png",
            OnClick = async () => await Electron.Dialog.ShowMessageBoxAsync("Notification clicked!!")
        };

        Electron.Notification.Show(options); 
